import _ from 'lodash';

export const all = [
  {
    id: 'red',
    title: 'Red',
    icon: 'danger',
    severity: 1000,
    nicknames: [
      'Danger Will Robinson! Danger!'
    ]
  },
  {
    id: 'uninitialized',
    title: 'Uninitialized',
    icon: 'spinner',
    severity: 900,
    nicknames: [
      'Initializing'
    ]
  },
  {
    id: 'yellow',
    title: 'Yellow',
    icon: 'warning',
    severity: 800,
    nicknames: [
      'S.N.A.F.U',
      'I\'ll be back',
      'brb'
    ]
  },
  {
    id: 'green',
    title: 'Green',
    icon: 'success',
    severity: 0,
    nicknames: [
      'Looking good'
    ]
  },
  {
    id: 'disabled',
    title: 'Disabled',
    severity: -1,
    icon: 'toggle-off',
    nicknames: [
      'Am I even a thing?'
    ]
  }
];

export const allById = _.indexBy(exports.all, 'id');

export const defaults = {
  icon: 'question',
  severity: Infinity
};

export function get(id) {
  return exports.allById[id] || _.defaults({ id: id }, exports.defaults);
}

